import Data.Char
import Data.List
import Data.List.Split

proccess_passes :: String -> [Int]
proccess_passes
  = map (toDec . toBin)
  . splitWhen (=='\n')

toDec :: String -> Int
toDec = foldl' (\acc x -> acc * 2 + digitToInt x) 0

toBin :: String -> String
toBin = map r
  where
    r 'F' = '0'
    r 'R' = '1'
    r 'B' = '1'
    r 'L' = '0'

partOne :: String -> Int
partOne
  = maximum
  . proccess_passes

-- F, R = 1
-- B, L = 0

-- I [0..2^10] \\ proccess_passes passes
-- then visually inspected instead of using
-- haskell to find the odd one out
partTwo :: String -> Int
partTwo = undefined

